from t_item as item
where 1=1

	and (name like
	CONCAT('%',#{searchName,jdbcType=VARCHAR},'%')
	)


	and (supervision_type_id like
	CONCAT('%',#{supervisionTypeId,jdbcType=INTEGER},'%')
	)


	and `preparer_time`>=#{schBeginTime,jdbcType=VARCHAR}


	and `preparer_time`<=#{schEndTime,jdbcType=VARCHAR}

