mapper.update:
update t_resource
set name = #{name,jdbcType=VARCHAR},
  xtype = #{xtype,jdbcType=INTEGER},
  resource = #{resource,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
  	SELECT 
  	FROM `t_resource` re WHERE 1=1

		and re.name = #{name,jdbcType=VARCHAR} 


		and re.resource = #{resource,jdbcType=VARCHAR}

SELECT r.*,f.name AS functionName 
FROM t_resource r, t_function f 
where moudle_id=#{moudleId,jdbcType=VARCHAR}

SELECT r.*,f.`name` AS functionName,f.`parent_id` AS parentId,f.url, (SELECT ff.name FROM t_function ff WHERE ff.id = f.`parent_id`)AS parentName FROM t_role_resource rr LEFT JOIN t_resource r ON rr.`resource_id` = r.`id` LEFT JOIN t_function f ON r.`moudle_id` = f.`id` WHERE rr.`role_id` in
#{item.id,jdbcType=INTEGER}
AND r.name IS NOT NULL ORDER BY f.`sort` ASC
